import React, { useState, useRef, useCallback, useEffect } from 'react';
import { FileText, Search, BarChart3, Map, List, Shield, Brain, AlertTriangle, Upload, CheckCircle, Loader2, Globe, Zap, Eye, Download, Calendar, Users, Activity, Target, MessageSquare, Lightbulb, Database, Clock, TrendingUp, XCircle, Filter, Maximize2, Layers } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, BarChart, Bar, PieChart, Pie, Cell, AreaChart, Area, ResponsiveContainer } from 'recharts';

// STYLES - Combining CSS files into a style tag
const GlobalStyles = () => (
  <style>{`
    body {
      margin: 0;
      font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
        'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue',
        sans-serif;
      -webkit-font-smoothing: antialiased;
      -moz-osx-font-smoothing: grayscale;
      background-color: #f8fafc;
    }
    code {
      font-family: source-code-pro, Menlo, Monaco, Consolas, 'Courier New',
        monospace;
    }
    .line-clamp-2 {
      display: -webkit-box;
      -webkit-line-clamp: 2;
      -webkit-box-orient: vertical;
      overflow: hidden;
    }
    .mapboxgl-popup-content {
      border-radius: 8px !important;
      box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06) !important;
    }
  `}</style>
);


// INTERFACES
export interface Document {
  id: string;
  content: string;
  metadata: {
    filename: string;
    file_type: string;
    uploaded_at: string;
    file_size: number;
  };
  analysis: {
    document_classification: {
      primary_type: string;
      sub_types: string[];
      confidence: number;
      security_classification: string;
    };
    entities: {
      persons: string[];
      organizations: string[];
      locations: string[];
      weapons: string[];
      vehicles: string[];
      dates: string[];
    };
    sentiment_analysis: {
      overall_sentiment: string;
      sentiment_score: number;
      threat_level: string;
      urgency_indicators: string[];
    };
    geographic_intelligence: {
      states: string[];
      cities: string[];
      countries: string[];
      coordinates: Array<{
        latitude: number;
        longitude: number;
        location_name: string;
        confidence: number;
      }>;
      total_locations: number;
      other_locations: string[];
    };
    temporal_intelligence: {
      dates_mentioned: string[];
      time_periods: string[];
      months_mentioned: string[];
      years_mentioned: string[];
      temporal_patterns: string[];
    };
    numerical_intelligence: {
      incidents: number[];
      casualties: number[];
      weapons: number[];
      arrests: number[];
      monetary_values: number[];
    };
    crime_patterns: {
      primary_crimes: Array<[string, number]>;
      crime_frequency: Record<string, number>;
      crime_trends: Array<{
        crime_type: string;
        trend: string;
        confidence: number;
      }>;
    };
    relationships: Array<{
      entity1: string;
      entity2: string;
      relationship_type: string;
      confidence: number;
    }>;
    text_statistics: {
      word_count: number;
      sentence_count: number;
      paragraph_count: number;
      readability_score: number;
      language: string;
    };
    intelligence_summary: string;
    confidence_score: number;
    processing_time: number;
  };
}

// --- IntelligenceAnalyzer Component ---
interface IntelligenceAnalyzerProps {
  onDocumentAnalyzed: (document: Document) => void;
}

const IntelligenceAnalyzer: React.FC<IntelligenceAnalyzerProps> = ({ onDocumentAnalyzed }) => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [error, setError] = useState<string | null>(null);
  const [dragActive, setDragActive] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleFileSelect = useCallback((file: File) => {
    setSelectedFile(file);
    setError(null);
  }, []);

  const handleDrop = useCallback((e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault(); e.stopPropagation(); setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFileSelect(e.dataTransfer.files[0]);
    }
  }, [handleFileSelect]);

  const handleDragOver = useCallback((e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault(); e.stopPropagation(); setDragActive(true);
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault(); e.stopPropagation(); setDragActive(false);
  }, []);

  const uploadAndAnalyze = async () => {
    if (!selectedFile) return;
    setUploading(true); setUploadProgress(0); setError(null);
    const formData = new FormData();
    formData.append('file', selectedFile);
    try {
      const response = await fetch('http://localhost:8000/upload-document', { method: 'POST', body: formData });
      if (!response.ok) { throw new Error(`HTTP error! status: ${response.status}`); }
      const data = await response.json();
      onDocumentAnalyzed(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Upload failed');
    } finally {
      setUploading(false);
    }
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
     <div className="p-6 space-y-6">
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
          <Upload className="h-5 w-5 text-blue-600 mr-2" />
          Document Upload & Analysis
        </h2>
        <div
          className={`relative border-2 border-dashed rounded-lg p-8 text-center transition-colors duration-200 ${dragActive ? 'border-blue-500 bg-blue-50' : 'border-gray-300 hover:border-gray-400'}`}
          onDrop={handleDrop} onDragOver={handleDragOver} onDragLeave={handleDragLeave} onClick={() => fileInputRef.current?.click()}
        >
          <input ref={fileInputRef} type="file" className="hidden" onChange={(e) => e.target.files?.[0] && handleFileSelect(e.target.files[0])} />
           {selectedFile ? (
              <>
                <CheckCircle className="h-12 w-12 text-green-600 mx-auto" />
                <p className="text-lg font-medium text-green-600">{selectedFile.name}</p>
                <p className="text-sm text-gray-500">{formatFileSize(selectedFile.size)}</p>
              </>
            ) : (
               <>
                <Upload className="h-12 w-12 text-gray-400 mx-auto" />
                <p className="text-lg font-medium text-gray-900">Drop document here or click to browse</p>
              </>
            )}
        </div>
        {error && <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-lg text-red-800">{error}</div>}
        <div className="mt-6">
          <button onClick={uploadAndAnalyze} disabled={!selectedFile || uploading} className="w-full bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 text-white font-medium py-3 px-6 rounded-lg flex items-center justify-center">
            {uploading ? <Loader2 className="h-5 w-5 mr-2 animate-spin" /> : <Brain className="h-5 w-5 mr-2" />}
            {uploading ? 'Analyzing...' : 'Start AI Analysis'}
          </button>
        </div>
      </div>
    </div>
  )
};

// --- QueryInterface Component ---
interface QueryInterfaceProps { onQueryResponse: (response: string) => void; }
interface QueryResponse { response: string; sources: any[]; }

const QueryInterface: React.FC<QueryInterfaceProps> = ({ onQueryResponse }) => {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!query.trim()) return;
    setLoading(true);
    try {
      const response = await fetch('http://localhost:8000/query', {
        method: 'POST', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query: query.trim(), k: 5 }),
      });
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data: QueryResponse = await response.json();
      onQueryResponse(data.response);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6">
      <form onSubmit={handleSubmit} className="space-y-4">
         <textarea value={query} onChange={(e) => setQuery(e.target.value)} placeholder="Ask a question about your documents..." className="w-full p-3 border rounded-lg" rows={4}/>
         <button type="submit" disabled={!query.trim() || loading} className="w-full bg-purple-600 hover:bg-purple-700 disabled:bg-gray-400 text-white font-medium py-3 rounded-lg flex items-center justify-center">
          {loading ? <Loader2 className="h-5 w-5 mr-2 animate-spin" /> : <Search className="h-5 w-5 mr-2" />}
          Query Intelligence
         </button>
      </form>
    </div>
  );
};

// --- AnalysisResults Component ---
interface AnalysisResultsProps { analysisData: Document | null; queryResponse: string; }
const AnalysisResults: React.FC<AnalysisResultsProps> = ({ analysisData, queryResponse }) => {
  if (!analysisData && !queryResponse) {
    return <div className="p-8 text-center text-gray-500">No analysis data available.</div>;
  }
  return (
    <div className="p-6 space-y-6">
      {analysisData && <div className="bg-white p-6 rounded-lg shadow">...Detailed Analysis...</div>}
      {queryResponse && <div className="bg-white p-6 rounded-lg shadow"><h3 className="font-bold mb-2">Query Response:</h3><p>{queryResponse}</p></div>}
    </div>
  );
};

// --- ForecastingDashboard Component ---
const ForecastingDashboard: React.FC = () => <div>Forecasting Dashboard (Placeholder)</div>;

// --- GeospatialMap Component ---
interface GeospatialMapProps { analysisData: Document | null; }
const GeospatialMap: React.FC<GeospatialMapProps> = ({ analysisData }) => {
  const mapContainer = useRef<HTMLDivElement>(null);
  useEffect(() => {
    if (!mapContainer.current || !(window as any).mapboxgl) {
        console.error("Mapbox GL JS is not loaded.");
        return;
    }
    const mapboxgl = (window as any).mapboxgl;
    mapboxgl.accessToken = 'pk.eyJ1IjoiYWJkdWxkYXR0aWpvMSIsImEiOiJjbWM2bW83Y3IwbmN4MmtzYWw0cHppbXF4In0.0pSWoI2VOyPyORqCROJc9g';
    const map = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [7.4, 9.0],
      zoom: 5
    });
    return () => map.remove();
  }, []);
  return <div ref={mapContainer} className="h-96 w-full" />;
};


// --- DocumentList Component ---
interface DocumentListProps { onDocumentSelect: (document: Document) => void; }
const DocumentList: React.FC<DocumentListProps> = ({ onDocumentSelect }) => {
  const [documents, setDocuments] = useState<any[]>([]);
  useEffect(() => {
    // Mock fetch
    fetch('http://localhost:8000/document-list').then(res => res.json()).then(data => setDocuments(data.documents || []));
  }, []);
  return (
    <div className="p-6 space-y-4">
      {documents.map(doc => (
        <div key={doc.id} className="p-4 bg-white rounded shadow cursor-pointer" onClick={() => onDocumentSelect(doc)}>
          {doc.filename}
        </div>
      ))}
    </div>
  )
};

// --- Main App Component ---
type TabType = 'analyzer' | 'query' | 'results' | 'forecasting' | 'map' | 'documents';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<TabType>('analyzer');
  const [analysisData, setAnalysisData] = useState<Document | null>(null);
  const [queryResponse, setQueryResponse] = useState<string>('');

  const handleDocumentAnalyzed = (document: Document) => {
    setAnalysisData(document);
    setActiveTab('results');
  };

  const handleDocumentSelect = (document: Document) => {
    setAnalysisData(document);
    setActiveTab('results');
  };

  const handleQueryResponse = (response: string) => {
    setQueryResponse(response);
    setActiveTab('results');
  };

  const tabs = [
    { id: 'analyzer' as TabType, label: 'Analyzer', icon: FileText },
    { id: 'query' as TabType, label: 'Query', icon: Search },
    { id: 'results' as TabType, label: 'Results', icon: Brain },
    { id: 'forecasting' as TabType, label: 'Forecasting', icon: BarChart3 },
    { id: 'map' as TabType, label: 'Map', icon: Map },
    { id: 'documents' as TabType, label: 'Library', icon: List }
  ];

  return (
    <>
      <GlobalStyles />
      <div className="min-h-screen bg-gray-50">
        <header className="bg-white border-b border-gray-200 shadow-sm">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex justify-between items-center py-4">
              <div className="flex items-center">
                <Shield className="h-8 w-8 text-blue-600 mr-3" />
                <div>
                  <h1 className="text-2xl font-bold text-gray-900">Intelligence Document Analyzer</h1>
                  <p className="text-sm text-gray-600">AI-Powered Security Intelligence Platform</p>
                </div>
              </div>
            </div>
          </div>
        </header>

        <nav className="bg-white border-b border-gray-200">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex space-x-8 overflow-x-auto">
              {tabs.map((tab) => {
                const Icon = tab.icon;
                const isActive = activeTab === tab.id;
                return (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`flex items-center space-x-2 py-4 px-1 border-b-2 font-medium text-sm whitespace-nowrap ${isActive ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500 hover:text-gray-700'}`}
                  >
                    <Icon className="h-5 w-5" />
                    <span>{tab.label}</span>
                  </button>
                );
              })}
            </div>
          </div>
        </nav>

        <main className="max-w-7xl mx-auto">
          {activeTab === 'analyzer' && <IntelligenceAnalyzer onDocumentAnalyzed={handleDocumentAnalyzed} />}
          {activeTab === 'query' && <QueryInterface onQueryResponse={handleQueryResponse} />}
          {activeTab === 'results' && <AnalysisResults analysisData={analysisData} queryResponse={queryResponse} />}
          {activeTab === 'forecasting' && <ForecastingDashboard />}
          {activeTab === 'map' && <GeospatialMap analysisData={analysisData} />}
          {activeTab === 'documents' && <DocumentList onDocumentSelect={handleDocumentSelect} />}
        </main>

        <footer className="bg-white border-t border-gray-200 mt-12">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
            <div className="flex justify-between items-center">
              <p className="text-sm text-gray-500">Â© 2025 Intelligence Document Analyzer.</p>
              {analysisData?.metadata?.uploaded_at && (
                  <div className="text-xs text-gray-400">
                    Last Analysis: {new Date(analysisData.metadata.uploaded_at).toLocaleString()}
                  </div>
                )}
            </div>
          </div>
        </footer>
      </div>
    </>
  );
};

export default App;
