import React, { useState, useEffect, useRef } from 'react';
import { MapPin, AlertTriangle, Activity, FileText, Globe } from 'lucide-react';
import * as d3 from 'd3';

const DynamicIntelligenceMap = () => {
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [locationHotspots, setLocationHotspots] = useState({});
  const mapRef = useRef(null);

  // Nigeria GeoJSON data (simplified)
  const nigeriaGeoJSON = {
    "type": "FeatureCollection",
    "features": [
      {
        "type": "Feature",
        "properties": { "name": "Nigeria" },
        "geometry": {
          "type": "Polygon",
          "coordinates": [[
            [2.6917, 6.2596], [2.7404, 8.7068], [3.5979, 10.7574], [4.2364, 11.1146],
            [5.2177, 11.7886], [7.0817, 12.1906], [8.7590, 12.0215], [10.4977, 11.8379],
            [11.0589, 12.1454], [12.1823, 12.7834], [13.3697, 12.4864], [14.1513, 12.2294],
            [14.8151, 11.5729], [14.3791, 10.9916], [14.9158, 10.0963], [14.8365, 9.5989],
            [14.9446, 8.7968], [14.5657, 8.0652], [14.4157, 7.0926], [13.5729, 6.5645],
            [12.8652, 6.1116], [11.7451, 6.9826], [10.5973, 7.0553], [9.2331, 6.4440],
            [8.5001, 4.7713], [7.4608, 4.4123], [6.6984, 3.3196], [5.8984, 3.2078],
            [5.3625, 4.8877], [4.9199, 5.9068], [4.0279, 6.2671], [2.9123, 6.2596],
            [2.6917, 6.2596]
          ]]
        }
      }
    ]
  };

  // Nigerian states with their approximate coordinates
  const nigerianStates = {
    'Lagos': { lat: 6.5244, lng: 3.3792 },
    'Ogun': { lat: 7.1608, lng: 3.3566 },
    'Oyo': { lat: 8.0000, lng: 4.0000 },
    'Osun': { lat: 7.5629, lng: 4.5200 },
    'Ekiti': { lat: 7.7193, lng: 5.3110 },
    'Ondo': { lat: 7.2500, lng: 5.2061 },
    'Edo': { lat: 6.3350, lng: 5.6037 },
    'Delta': { lat: 5.8900, lng: 5.6800 },
    'Rivers': { lat: 4.8156, lng: 6.9994 },
    'Bayelsa': { lat: 4.7719, lng: 6.0699 },
    'Cross River': { lat: 5.9630, lng: 8.3400 },
    'Akwa Ibom': { lat: 5.0077, lng: 7.8499 },
    'Abia': { lat: 5.4200, lng: 7.4900 },
    'Imo': { lat: 5.4840, lng: 7.0347 },
    'Anambra': { lat: 6.2106, lng: 6.9978 },
    'Enugu': { lat: 6.5244, lng: 7.5106 },
    'Ebonyi': { lat: 6.2649, lng: 8.0137 },
    'Benue': { lat: 7.3390, lng: 8.7539 },
    'Kogi': { lat: 7.7989, lng: 6.7393 },
    'Nasarawa': { lat: 8.5378, lng: 8.3206 },
    'Plateau': { lat: 9.2182, lng: 9.5179 },
    'FCT': { lat: 9.0765, lng: 7.3986 }, // Abuja
    'Niger': { lat: 10.0000, lng: 5.5000 },
    'Kwara': { lat: 8.9669, lng: 4.3841 },
    'Kaduna': { lat: 10.6093, lng: 7.4298 },
    'Katsina': { lat: 12.9908, lng: 7.6177 },
    'Kano': { lat: 12.0022, lng: 8.5920 },
    'Jigawa': { lat: 12.4534, lng: 9.3452 },
    'Yobe': { lat: 11.7466, lng: 11.9661 },
    'Borno': { lat: 11.8846, lng: 13.1571 },
    'Adamawa': { lat: 9.3265, lng: 12.3984 },
    'Taraba': { lat: 8.8921, lng: 11.3588 },
    'Bauchi': { lat: 10.3158, lng: 9.8442 },
    'Gombe': { lat: 10.2897, lng: 11.1711 },
    'Kebbi': { lat: 12.4539, lng: 4.1975 },
    'Sokoto': { lat: 13.0059, lng: 5.2476 },
    'Zamfara': { lat: 12.1700, lng: 6.2200 }
  };

  // Process uploaded documents to extract location data
  useEffect(() => {
    const processLocationData = () => {
      const hotspots = {};

      uploadedFiles.forEach(file => {
        if (file.analysis && file.analysis.geographic_intelligence) {
          const states = file.analysis.geographic_intelligence.states || [];
          const numericalData = file.analysis.numerical_intelligence || {};

          states.forEach(state => {
            if (nigerianStates[state]) {
              if (!hotspots[state]) {
                hotspots[state] = {
                  incidents: 0,
                  casualties: 0,
                  documents: 0,
                  threatLevel: 'Low',
                  crimeTags: new Set()
                };
              }

              hotspots[state].documents += 1;
              hotspots[state].incidents += Math.max(...(numericalData.incidents || [0]));
              hotspots[state].casualties += Math.max(...(numericalData.casualties || [0]));

              const totalIncidents = hotspots[state].incidents;
              if (totalIncidents > 100) hotspots[state].threatLevel = 'Critical';
              else if (totalIncidents > 50) hotspots[state].threatLevel = 'High';
              else if (totalIncidents > 10) hotspots[state].threatLevel = 'Medium';

              if (file.analysis.crime_patterns && file.analysis.crime_patterns.primary_crimes) {
                file.analysis.crime_patterns.primary_crimes.forEach(([crime, count]) => {
                  if (count > 0) hotspots[state].crimeTags.add(crime.replace('_', ' '));
                });
              }
            }
          });
        }
      });

      Object.keys(hotspots).forEach(state => {
        hotspots[state].crimeTags = Array.from(hotspots[state].crimeTags);
      });

      setLocationHotspots(hotspots);
    };

    processLocationData();
  }, [uploadedFiles]);

  // D3 Map Rendering
  useEffect(() => {
    if (!mapRef.current) return;

    const svg = d3.select(mapRef.current);
    svg.selectAll("*").remove(); // Clear previous render

    const width = 800;
    const height = 500;

    svg.attr("viewBox", `0 0 ${width} ${height}`)
       .attr("width", "100%")
       .attr("height", "100%");

    // Create projection for Nigeria
    const projection = d3.geoMercator()
      .center([8, 9.5]) // Center on Nigeria
      .scale(2000)
      .translate([width / 2, height / 2]);

    const path = d3.geoPath().projection(projection);

    // Draw Nigeria
    svg.selectAll(".country")
       .data(nigeriaGeoJSON.features)
       .enter()
       .append("path")
       .attr("class", "country")
       .attr("d", path)
       .attr("fill", "#e5e7eb")
       .attr("stroke", "#6b7280")
       .attr("stroke-width", 2);

    // Add state boundaries (simplified grid)
    const stateLines = [
      [[3, 6], [14, 6]], // South boundary
      [[3, 8], [14, 8]], // Middle boundary
      [[3, 10], [14, 10]], // North boundary
      [[6, 4], [6, 13]], // West-East line 1
      [[9, 4], [9, 13]], // West-East line 2
      [[12, 4], [12, 13]] // West-East line 3
    ];

    svg.selectAll(".state-boundary")
       .data(stateLines)
       .enter()
       .append("path")
       .attr("class", "state-boundary")
       .attr("d", d => {
         const start = projection([d[0][0], d[0][1]]);
         const end = projection([d[1][0], d[1][1]]);
         return `M ${start[0]},${start[1]} L ${end[0]},${end[1]}`;
       })
       .attr("stroke", "#9ca3af")
       .attr("stroke-width", 1)
       .attr("opacity", 0.5);

    // Add hotspot markers
    Object.entries(locationHotspots).forEach(([state, data]) => {
      const coords = nigerianStates[state];
      if (!coords) return;

      const [x, y] = projection([coords.lng, coords.lat]);

      // Hotspot circle
      const radius = Math.max(4, Math.min(20, data.incidents / 5));
      const color = getThreatColor(data.threatLevel);

      svg.append("circle")
         .attr("cx", x)
         .attr("cy", y)
         .attr("r", radius)
         .attr("fill", color)
         .attr("stroke", "white")
         .attr("stroke-width", 2)
         .attr("opacity", 0.8)
         .style("cursor", "pointer")
         .on("click", () => {
           alert(`${state} State
Threat Level: ${data.threatLevel}
Incidents: ${data.incidents}
Casualties: ${data.casualties}
Documents: ${data.documents}
Crime Types: ${data.crimeTags.join(', ')}`);
         });

      // Pulsing effect for critical areas
      if (data.threatLevel === 'Critical') {
        svg.append("circle")
           .attr("cx", x)
           .attr("cy", y)
           .attr("r", radius + 5)
           .attr("fill", "none")
           .attr("stroke", color)
           .attr("stroke-width", 2)
           .attr("opacity", 0.6)
           .append("animate")
           .attr("attributeName", "r")
           .attr("values", `${radius};${radius + 10};${radius}`)
           .attr("dur", "2s")
           .attr("repeatCount", "indefinite");
      }

      // State label
      svg.append("text")
         .attr("x", x)
         .attr("y", y + radius + 15)
         .attr("text-anchor", "middle")
         .attr("font-size", "10px")
         .attr("font-weight", "bold")
         .attr("fill", "#374151")
         .text(state);

      // Incident count
      svg.append("text")
         .attr("x", x)
         .attr("y", y + radius + 27)
         .attr("text-anchor", "middle")
         .attr("font-size", "8px")
         .attr("fill", "#6b7280")
         .text(data.incidents);
    });

    // Add major cities
    const majorCities = [
      { name: "Lagos", lat: 6.5244, lng: 3.3792 },
      { name: "Abuja", lat: 9.0765, lng: 7.3986 },
      { name: "Kano", lat: 12.0022, lng: 8.5920 },
      { name: "Port Harcourt", lat: 4.8156, lng: 6.9994 }
    ];

    majorCities.forEach(city => {
      const [x, y] = projection([city.lng, city.lat]);
      svg.append("circle")
         .attr("cx", x)
         .attr("cy", y)
         .attr("r", 3)
         .attr("fill", "#1f2937")
         .attr("opacity", 0.7);

      svg.append("text")
         .attr("x", x + 5)
         .attr("y", y - 5)
         .attr("font-size", "9px")
         .attr("fill", "#1f2937")
         .text(city.name);
    });

    // No data message
    if (Object.keys(locationHotspots).length === 0) {
      svg.append("rect")
         .attr("x", width/2 - 150)
         .attr("y", height/2 - 50)
         .attr("width", 300)
         .attr("height", 100)
         .attr("fill", "white")
         .attr("fill-opacity", 0.9)
         .attr("stroke", "#d1d5db")
         .attr("rx", 10);

      svg.append("text")
         .attr("x", width/2)
         .attr("y", height/2 - 20)
         .attr("text-anchor", "middle")
         .attr("font-size", "14px")
         .attr("font-weight", "bold")
         .attr("fill", "#6b7280")
         .text("No documents analyzed yet");

      svg.append("text")
         .attr("x", width/2)
         .attr("y", height/2 + 5)
         .attr("text-anchor", "middle")
         .attr("font-size", "12px")
         .attr("fill", "#9ca3af")
         .text("Upload intelligence documents to see hotspots");
    }

  }, [locationHotspots]);

  const handleFileSelect = (event) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
      setAnalysisResult(null);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    setUploading(true);

    setTimeout(() => {
      const mockResult = {
        document_id: `doc_${Date.now()}`,
        analysis: {
          confidence_score: 0.85 + Math.random() * 0.15,
          intelligence_summary: `Document reports ${Math.floor(Math.random() * 50 + 10)} incidents across multiple locations with ${Math.floor(Math.random() * 30 + 5)} casualties recorded`,
          document_classification: { primary_type: 'intelligence_report' },
          geographic_intelligence: {
            states: ['Zamfara', 'Katsina', 'Kaduna', 'Plateau', 'Borno'].slice(0, Math.floor(Math.random() * 4) + 1),
            total_locations: Math.floor(Math.random() * 5) + 1
          },
          sentiment_analysis: {
            threat_level: ['High', 'Medium', 'Critical'][Math.floor(Math.random() * 3)]
          },
          numerical_intelligence: {
            incidents: [Math.floor(Math.random() * 100 + 10)],
            casualties: [Math.floor(Math.random() * 50 + 5)],
            arrests: [Math.floor(Math.random() * 30 + 2)]
          },
          crime_patterns: {
            primary_crimes: [
              ['armed_robbery', Math.floor(Math.random() * 20)],
              ['cattle_rustling', Math.floor(Math.random() * 15)],
              ['kidnapping', Math.floor(Math.random() * 10)]
            ]
          }
        }
      };

      setAnalysisResult(mockResult);
      setUploadedFiles(prev => [...prev, {
        id: mockResult.document_id,
        name: selectedFile.name,
        uploadedAt: new Date().toLocaleString(),
        analysis: mockResult.analysis
      }]);
      setSelectedFile(null);
      setUploading(false);
    }, 2000);
  };

  const getThreatColor = (threatLevel) => {
    switch (threatLevel) {
      case 'Critical': return '#dc2626';
      case 'High': return '#ea580c';
      case 'Medium': return '#d97706';
      default: return '#16a34a';
    }
  };

  return (
    <div className="w-full max-w-6xl mx-auto p-6 bg-white">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-800 mb-2 flex items-center">
          <Globe className="h-6 w-6 mr-2 text-blue-600" />
          Intelligence Hotspot Map - Nigeria
        </h2>
        <p className="text-gray-600">Professional geographic visualization using D3.js with real-time document analysis</p>
      </div>

      {/* Document Upload Section */}
      <div className="mb-6 p-4 border-2 border-dashed border-gray-300 rounded-lg">
        <div className="text-center">
          <FileText className="h-8 w-8 text-gray-400 mx-auto mb-2" />
          <p className="text-sm text-gray-600 mb-4">Upload intelligence documents to populate the map with real hotspots</p>

          <input
            type="file"
            accept=".pdf,.doc,.docx,.txt,.jpg,.jpeg,.png"
            onChange={handleFileSelect}
            className="hidden"
            id="file-upload"
          />
          <label
            htmlFor="file-upload"
            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 cursor-pointer"
          >
            Choose Document
          </label>

          {selectedFile && (
            <div className="mt-4 flex items-center justify-between max-w-md mx-auto p-3 bg-gray-50 rounded">
              <span className="text-sm text-gray-700">{selectedFile.name}</span>
              <button
                onClick={handleUpload}
                disabled={uploading}
                className="px-3 py-1 bg-green-600 text-white rounded text-sm hover:bg-green-700 disabled:opacity-50"
              >
                {uploading ? 'Processing...' : 'Analyze'}
              </button>
            </div>
          )}
        </div>
      </div>

      {/* D3.js Map Container */}
      <div className="bg-gray-50 rounded-lg p-6 mb-6">
        <svg ref={mapRef} className="w-full border rounded bg-blue-50" style={{ minHeight: '400px' }}></svg>

        {/* Map Legend */}
        <div className="mt-4 grid grid-cols-2 md:grid-cols-4 gap-4">
          <div className="flex items-center space-x-2">
            <div className="w-4 h-4 bg-red-600 rounded-full"></div>
            <span className="text-sm">Critical Risk</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-4 h-4 bg-orange-600 rounded-full"></div>
            <span className="text-sm">High Risk</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-4 h-4 bg-yellow-600 rounded-full"></div>
            <span className="text-sm">Medium Risk</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-4 h-4 bg-green-600 rounded-full"></div>
            <span className="text-sm">Low Risk</span>
          </div>
        </div>

        <div className="mt-3 p-3 bg-blue-50 rounded-lg">
          <p className="text-sm text-blue-800">
            <strong>Professional Mapping:</strong> Powered by D3.js with geographic projections and real coordinates.
            Hotspots are generated from document analysis. Click any marker for detailed intelligence.
          </p>
        </div>
      </div>

      {/* Document Analysis Results */}
      {uploadedFiles.length > 0 && (
        <div>
          <h3 className="text-lg font-semibold mb-4 flex items-center">
            <Activity className="h-5 w-5 mr-2 text-green-600" />
            Processed Documents ({uploadedFiles.length})
          </h3>
          <div className="space-y-3">
            {uploadedFiles.map((file, index) => (
              <div key={index} className="flex items-center justify-between p-4 bg-gray-50 rounded border">
                <div>
                  <p className="font-medium">{file.name}</p>
                  <p className="text-sm text-gray-500">{file.uploadedAt}</p>
                  <p className="text-sm text-blue-600">
                    States: {file.analysis.geographic_intelligence?.states?.join(', ') || 'None detected'}
                  </p>
                </div>
                <div className="text-right">
                  <p className="text-sm font-medium text-green-600">
                    {file.analysis.sentiment_analysis?.threat_level} Threat
                  </p>
                  <p className="text-xs text-gray-500">
                    Confidence: {(file.analysis.confidence_score * 100).toFixed(0)}%
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Real-time Analysis Summary */}
      {Object.keys(locationHotspots).length > 0 && (
        <div className="mt-6 p-4 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-lg border border-blue-200">
          <h3 className="text-lg font-semibold mb-3 text-blue-900">Live Intelligence Summary</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="text-center">
              <p className="text-2xl font-bold text-blue-600">{Object.keys(locationHotspots).length}</p>
              <p className="text-sm text-gray-600">Active Hotspots</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-red-600">
                {Object.values(locationHotspots).reduce((sum, loc) => sum + loc.incidents, 0)}
              </p>
              <p className="text-sm text-gray-600">Total Incidents</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-green-600">{uploadedFiles.length}</p>
              <p className="text-sm text-gray-600">Documents Analyzed</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DynamicIntelligenceMap;
